const categorias=document.querySelectorAll("#categorias .categoria"),contenedorPreguntas=document.querySelectorAll(".contenedor-preguntas");let categoriaActiva=null;categorias.forEach(a=>{a.addEventListener("click",t=>{categorias.forEach(a=>{a.classList.remove("activa")}),t.currentTarget.classList.toggle("activa"),categoriaActiva=a.dataset.categoria,contenedorPreguntas.forEach(a=>{a.dataset.categoria===categoriaActiva?a.classList.add("activo"):a.classList.remove("activo")})})});